Ext.define('editAccount.view.MainWindowViewController', {
  extend: 'Ext.app.ViewController',
  alias: 'controller.mainwindow',

  init: function () {
    var login = JSON.parse(localStorage.getItem("login"));
    if(!login){
      window.location.replace(FACADE_URL+'/login.html');
    }
    var viewModel = this.getViewModel();
    viewModel.set('formData',{
      userName:'admin',
      password:'',
      newPassword:'',
      confirmPassword:''
    })

  },

  /**
   * 保存
   */
  onSaveButtonClick: function (button, e, eOpts) {
    var viewCtr = this,
        currWin = viewCtr.getView(),
        viewModel = this.getViewModel(),
        formCmp = currWin.down("form");

    var formData = viewModel.get('formData'),
        userName = formData.userName,
        password = formData.password,
        newPassword = formData.newPassword,
        confirmPassword = formData.confirmPassword;

    if(!password){
      TipsUtil.showTips("错误", "请输入原密码");
      return;
    }
    if(!newPassword){
      TipsUtil.showTips("错误", "请输入新密码");
      return;
    }
    if(!confirmPassword){
      TipsUtil.showTips("错误", "请输入确认密码");
      return;
    }
    if(newPassword != confirmPassword){
      TipsUtil.showTips("错误","新密码和确认密码不匹配");
      return
    }

    var url = FACADE_URL+'/account/updatePassword';
      Ext.Ajax.request({
        method: 'POST',
        url: url,
        params: {
          userName:userName,
          password:password,
          newPassword:newPassword
        },
        success: function (request) {
          if (request.responseText) {
            var json = Ext.decode(request.responseText);
            if (json.success) {
              TipsUtil.showTips("提示", "更新成功", TipsUtil.INFO);
              formCmp.getForm().reset();
              return;
            } else {
              TipsUtil.showTips("错误", json.error.message || "服务器错误！");
            }
          }
        }
      });
  },

  companyChange:function(field, newValue, oldValue, eOpts){
    var viewModel = this.getViewModel();
    if(!newValue) return;

    var url = FACADE_URL+'/company/view/'+newValue;
    Ext.Ajax.request({
      method: 'GET',
      url: url,
      success: function (request) {
        if (request.responseText) {
          var json = Ext.decode(request.responseText);
          if (json.success) {
            viewModel.set('companyCode',json.data.code);
            viewModel.set('companyName',json.data.name);
            return;
          } else {
            TipsUtil.showTips("错误", json.error.message || "服务器错误！");
          }
        }
      }
    });
  },

  roleChange:function(field, newValue, oldValue, eOpts){
    var viewModel = this.getViewModel();

    viewModel.set('roleName',field.lastMutatedValue);
    console.info(field);
  }
});