Ext.define('addAccount.view.MainWindowViewController', {
  extend: 'Ext.app.ViewController',
  alias: 'controller.mainwindow',

  init: function () {
    var login = JSON.parse(localStorage.getItem("login"));
    if(!login){
      window.location.replace(FACADE_URL+'/login.html');
    }
  },

  /**
   * 保存
   */
  onSaveButtonClick: function (button, e, eOpts) {
    var viewCtr = this,
        currWin = viewCtr.getView(),
        viewModel = this.getViewModel(),
        formCmp = currWin.down("form");
    if (!formCmp.isValid()) {
      TipsUtil.showTips("提示", "信息填写有误", TipsUtil.WARING);
      return ;
    }
    var formData = formCmp.getForm().getValues();
    formData.companyName = viewModel.get('companyName');
    formData.roleName = viewModel.get('roleName');
    //formData.userId = JSON.parse(localStorage.getItem('accountId'));
    var url = FACADE_URL+'/account/add';
      Ext.Ajax.request({
        method: 'POST',
        url: url,
        params: formData,
        success: function (request) {
          if (request.responseText) {
            var json = Ext.decode(request.responseText);
            if (json.success) {
              TipsUtil.showTips("提示", "新增成功", TipsUtil.INFO);
              formCmp.getForm().reset();
              return;
            } else {
              TipsUtil.showTips("错误", json.error.message || "服务器错误！");
            }
          }
        }
      });
  },

  companyChange:function(field, newValue, oldValue, eOpts){
    var viewModel = this.getViewModel();
    if(!newValue) return;

    var url = FACADE_URL+'/company/view/'+newValue;
    Ext.Ajax.request({
      method: 'GET',
      url: url,
      success: function (request) {
        if (request.responseText) {
          var json = Ext.decode(request.responseText);
          if (json.success) {
            viewModel.set('companyCode',json.data.code);
            viewModel.set('companyName',json.data.name);
            return;
          } else {
            TipsUtil.showTips("错误", json.error.message || "服务器错误！");
          }
        }
      }
    });
  },

  roleChange:function(field, newValue, oldValue, eOpts){
    var viewModel = this.getViewModel();

    viewModel.set('roleName',field.lastMutatedValue);
    console.info(field);
  }
});