Ext.define('ExpressMgmt.view.DeliveryTemplateChooseWindowViewController', {
  extend: 'Ext.app.ViewController',
  alias: 'controller.deliverytemplatechoosewindow',

  onButtonSelectClick: function (button, e, eOpts) {
    var viewModel = this.getViewModel();
    var grid = this.getView().down('grid');
    var selection = grid.getSelectionModel().getSelection();
    if (!selection || !selection.length) {
      Ext.MessageBox.alert('提示', '请选择一行');
      return;
    }
    var selectedData = selection[0].data;
    viewModel.setData(selectedData);
    this.getView().close();
  },
  itemdblclick: function () {
    this.onButtonSelectClick();
  },

  onButtonCancelClick: function (button, e, eOpts) {
    this.getView().close();
  }
});
