Ext.define('PickLocation.view.PickLocationViewPortViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.picklocationviewport',
    afterGridPanelRender: function(component, eOpts) {
    },

    /**
     * 快速搜索
     */
    onFastQueryButtonClick: function (field, trigger, e) {
        var viewCtr = this;
        viewCtr._onFastSearchFn(field);
    },

    /**
     * 快速搜索回车
     */
    onFastSearchTextFieldSpecialKey: function(field, e, options){
        var viewCtr = this;
        if (e.getKey() === e.ENTER) {
            viewCtr._onFastSearchFn(field);
        }
    },
    /**
     * 快速查询
     * @private
     */
    _onFastSearchFn: function(field) {
        var viewCtr = this;
        var gridStore = viewCtr.getView().down('grid').store;
        var text = field.getValue();
        gridStore.getProxy().setExtraParam("query", text);
        gridStore.load();
    },
    /**
     * 导入库位
     */
    uploadPickLocation: function(button, e, eOpts){
        Ext.create('PickLocation.view.Uploadwindow').show();
    },
    exportPickLocation: function(button, e, eOpts){
        var viewmodel = this.getViewModel();
        var query =  viewmodel.data.searchOpts.query;
        window.location.href = '/api/inventory/warehouse/pickloc/export-.html?query='+ query;
    },
    onGridpanelRowClick: function(tableview, record, tr, rowIndex, e, eOpts) {
        var pickLocViewModel = this.getViewModel();
        var pickStore = pickLocViewModel.getStore('pickStore');
        var skuId = record.get('skuId');
        var skuCode = record.get('skuCode');
        pickStore.getProxy().setExtraParams({skuId: skuId,skuCode: skuCode});
        pickStore.load();
    },
    onGridAfterRender: function(component, eOpts) {
        /*var selectionModel = component.getSelectionModel();
        selectionModel.selectRange(0,0,true);*/


    },
    onGridpanelViewReady: function(tablepanel, eOpts) {
        var selectionModel = tablepanel.getSelectionModel();
        selectionModel.selectRange(0,0,true);
    },

    validateEdit: function(editor,e, eopts){
        var oldValue = e.record.data[e.field];
        var newValue = e.value;
        if(!newValue)return;
        if(newValue.length>50){
            TipsUtil.showTips('提示','长度限制为50个字符','info');
            return false;
        }
    }

});
